$(function(){$(".use-js").on("mouseenter",function(){switch($(this).attr("data-animate")){case"plus":$(this).addClass("use-plus");break;case"buzz":$(this).addClass("use-buzz");break;case"wobble":$(this).addClass("use-wobble")}}).on("mouseleave",function(){switch($(this).attr("data-animate")){case"plus":$(this).removeClass("use-plus");break;case"buzz":$(this).removeClass("use-buzz");break;case"wobble":$(this).removeClass("use-wobble")}})});